// Example of Splash, Login and Sign Up in React Native
// https://aboutreact.com/react-native-login-and-signup/

// Import React and Component
import React, { useState, useEffect } from 'react';
import { StyleSheet, View, Text, SafeAreaView, ScrollView } from 'react-native';
import { DataTable } from 'react-native-paper';

import AsyncStorage from '@react-native-community/async-storage';

import axios from 'axios';
const http = axios.create({ baseURL: 'http://localhost:5000/api', withCredentials: true })

const HomeScreen = () => {
  const [user_id, setUser_id] = useState('');
  const [user_name, setUser_name] = useState('');
  const data = [
    {
      NAME: "hahaha",
      QTY: 1,
      AMT: 1,
      TAX: 1,
      GROSS: 1
    }
  ]
  // const [user_email, setUser_email] = useState('');
  // const [user_pwd, setUser_pwd] = useState('');
  useEffect(() => {
    // AsyncStorage.getItem('user_id').then((value) => setUser_id(value))
    // AsyncStorage.getItem('user_name').then((value) => setUser_name(value))
    // AsyncStorage.getItem('user_email').then((value) => setUser_email(value))
    // AsyncStorage.getItem('user_pwd').then((value) => setUser_pwd(value))

    http.get('/userData').then(
      response => response.data
    ).then((responseJson) => {
      console.log("responseJson", responseJson)
      if (responseJson) {
        setUser_id(responseJson.id);
        setUser_name(responseJson.name);
      }
    }).catch(function (error) {
      console.log(error);
    });

    // fetch('http://192.168.1.2:5000/clinicData', {
    //   method: 'GET',
    //   headers: {
    //     'Accept': 'application/json',
    //     'Content-Type': 'application/json'
    //   },
    //   credentials: 'same-origin'
    // })
    //   .then((response) => response.json())
    //   .then((responseJson) => {
    //     if (responseJson.data.length >= 1) {
    //       console.log("responseJson.data",responseJson.data);
    //     } else {
    //       console.log("responseJson.data",responseJson.data);
    //     }
    //   })
    //   .catch((error) => {
    //     //Hide Loader
    //     setLoading(false);
    //     console.error(error);
    //   });

  }, []);
  return (
    <SafeAreaView style={{ flex: 1 }}>
      <DataTable style={{ height: 1 }}>
        <DataTable.Header>
          <DataTable.Title>Name</DataTable.Title>
          <DataTable.Title numeric>QTY</DataTable.Title>
          <DataTable.Title numeric>AMT</DataTable.Title>
          <DataTable.Title numeric>TAX</DataTable.Title>
          <DataTable.Title numeric>GROSS</DataTable.Title>
        </DataTable.Header>
        <View>
          <ScrollView>
            {
              data && data.length > 0 ?
                data.map((item, index) => {
                  console.log("item.NAME",item.NAME)
                  return (
                    <DataTable.Row key={index}>
                      <DataTable.Cell>{item.NAME}</DataTable.Cell>
                      <DataTable.Cell numeric>{item.QTY}</DataTable.Cell>
                      <DataTable.Cell numeric>{item.AMT}</DataTable.Cell>
                      <DataTable.Cell numeric>{item.TAX}</DataTable.Cell>
                      <DataTable.Cell numeric>{item.GROSS}</DataTable.Cell>
                    </DataTable.Row>
                  );
                })
                :
                false
            }
          </ScrollView>
        </View>
      </DataTable>
      <View style={{ flex: 1, padding: 16 }}>

        <Text
          style={{
            fontSize: 18,
            textAlign: 'center',
            color: 'grey',
          }}>
          Splash, Login and Register Example{'\n'}React Native
        </Text>
        <Text
          style={{
            fontSize: 16,
            textAlign: 'center',
            color: 'grey',
          }}>
          www.aboutreact.com
        </Text>
      </View>
    </SafeAreaView>
  );
};

export default HomeScreen;
